					var __h = Math.max((_image.Image.height * AscCommon.g_dKoef_pix_to_mm), 1);

					var dKoef = Math.max(__w / _w, __h / _h);
					if (dKoef > 1) {
						_w = Math.max(5, __w / dKoef);
						_h = Math.max(5, __h / dKoef);

						bIsCorrect = true;
					} else {
						_w = __w;
						_h = __h;
					}
